@tailwind base;
@tailwind components;
@tailwind utilities;

/* Definition of the design system. All colors, gradients, fonts, etc should be defined here. 
All colors MUST be HSL.
*/

@layer base {
  :root {
    --background: 0 0% 100%;
    --foreground: 220 13% 13%;

    --card: 0 0% 100%;
    --card-foreground: 220 13% 13%;

    --popover: 0 0% 100%;
    --popover-foreground: 220 13% 13%;

    /* Professional blue primary */
    --primary: 220 90% 56%;
    --primary-foreground: 0 0% 100%;
    --primary-hover: 220 90% 48%;

    /* Vibrant orange accent for CTAs */
    --accent: 25 95% 53%;
    --accent-foreground: 0 0% 100%;
    --accent-hover: 25 95% 45%;

    --secondary: 220 14% 96%;
    --secondary-foreground: 220 13% 13%;

    --muted: 220 14% 96%;
    --muted-foreground: 220 9% 46%;

    --destructive: 0 84% 60%;
    --destructive-foreground: 0 0% 100%;

    --border: 220 13% 91%;
    --input: 220 13% 91%;
    --ring: 220 90% 56%;

    --radius: 0.75rem;

    /* Gradients */
    --gradient-hero: linear-gradient(135deg, hsl(220 90% 56%) 0%, hsl(220 90% 48%) 100%);
    --gradient-card: linear-gradient(180deg, hsl(0 0% 100%) 0%, hsl(220 14% 98%) 100%);
    
    /* Shadows */
    --shadow-card: 0 1px 3px 0 rgb(0 0 0 / 0.1), 0 1px 2px -1px rgb(0 0 0 / 0.1);
    --shadow-card-hover: 0 10px 25px -5px rgb(0 0 0 / 0.1), 0 8px 10px -6px rgb(0 0 0 / 0.1);
    
    /* Transitions */
    --transition-smooth: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .dark {
    --background: 220 18% 8%;
    --foreground: 220 14% 96%;

    --card: 220 18% 12%;
    --card-foreground: 220 14% 96%;

    --popover: 220 18% 12%;
    --popover-foreground: 220 14% 96%;

    --primary: 220 90% 56%;
    --primary-foreground: 0 0% 100%;
    --primary-hover: 220 90% 64%;

    --accent: 25 95% 53%;
    --accent-foreground: 0 0% 100%;
    --accent-hover: 25 95% 61%;

    --secondary: 220 18% 16%;
    --secondary-foreground: 220 14% 96%;

    --muted: 220 18% 16%;
    --muted-foreground: 220 9% 65%;

    --destructive: 0 84% 60%;
    --destructive-foreground: 0 0% 100%;

    --border: 220 18% 20%;
    --input: 220 18% 20%;
    --ring: 220 90% 56%;

    --gradient-hero: linear-gradient(135deg, hsl(220 90% 48%) 0%, hsl(220 90% 40%) 100%);
    --gradient-card: linear-gradient(180deg, hsl(220 18% 12%) 0%, hsl(220 18% 14%) 100%);
    
    --shadow-card: 0 1px 3px 0 rgb(0 0 0 / 0.3), 0 1px 2px -1px rgb(0 0 0 / 0.3);
    --shadow-card-hover: 0 10px 25px -5px rgb(0 0 0 / 0.3), 0 8px 10px -6px rgb(0 0 0 / 0.3);
  }
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground antialiased;
  }
}

@layer utilities {
  .gradient-hero {
    background: var(--gradient-hero);
  }
  
  .gradient-card {
    background: var(--gradient-card);
  }
  
  .shadow-card {
    box-shadow: var(--shadow-card);
  }
  
  .shadow-card-hover {
    box-shadow: var(--shadow-card-hover);
  }
  
  .transition-smooth {
    transition: var(--transition-smooth);
  }
}
