@@ -0,0 +1,152 @@
+// src/pages/Dashboard.tsx
+import { useState, useMemo, useEffect } from "react";
+import CarCard from "@/components/CarCard";
+import FilterPanel from "@/components/FilterPanel";
+import { filterCars, FilterOptions } from "@/lib/filterUtils";
+import { Input } from "@/components/ui/input";
+import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
+import { Search } from "lucide-react";
+import heroImage from "@/assets/hero-car.jpg";
+import { getAllCars, API_BASE_URL, ApiCar } from "@/api/cars";
+
+// Clean UI Car (no location)
+export interface UICar {
+  id: string;
+  title: string;
+  brand: string;
+  model: string;
+  year: number;
+  price: number;
+  mileage: number;
+  condition: string;
+  description: string;
+  images: string[];
+  platform: { name: string }[];
+  seller: { id: string; name: string; email: string; phone: string };
+  createdAt: string;
+  fuelType?: string;
+  transmission?: string;
+}
+
+// Mapper
+const mapApiCarToUICar = (car: ApiCar): UICar => ({
+  id: car._id,
+  title: `${car.brand} ${car.model} ${car.year}`,
+  brand: car.brand,
+  model: car.model,
+  year: car.year,
+  price: car.price,
+  mileage: car.mileage,
+  condition: car.condition,
+  description: car.description,
+  images: car.images.map((img) => `${API_BASE_URL}/uploads/${img}`),
+  platform: (car.platforms || []).map((p) => ({ name: p })),
+  seller: {
+    id: car.seller?.id || "",
+    name: car.seller?.name || "Private Seller",
+    email: car.seller?.email || "N/A",
+    phone: "N/A",
+  },
+  createdAt: car.createdAt,
+});
+
+const Dashboard = () => {
+  const [filters, setFilters] = useState<FilterOptions>({
+    searchTerm: "",
+    brands: [],
+    minPrice: 0,
+    maxPrice: 100000,
+    minYear: 2015,
+    maxYear: 2025,
+    minMileage: 0,
+    maxMileage: 150000,
+    conditions: [],
+    fuelTypes: [],
+    transmissions: [],
+    sortBy: "newest",
+  });
+
+  const [cars, setCars] = useState<UICar[]>([]);
+  const [loading, setLoading] = useState(true);
+
+  useEffect(() => {
+    const fetchCars = async () => {
+      try {
+        const apiCars = await getAllCars();
+        setCars(apiCars.map(mapApiCarToUICar));
+      } catch (err) {
+        console.error("Failed to load cars", err);
+      } finally {
+        setLoading(false);
+      }
+    };
+    fetchCars();
+  }, []);
+
+  const availableBrands = useMemo(() => Array.from(new Set(cars.map((c) => c.brand))).sort(), [cars]);
+  const filteredCars = useMemo(() => filterCars(cars, filters), [cars, filters]);
+
+  return (
+    <div className="min-h-screen">
+      {/* Hero */}
+      <section className="relative h-[500px] overflow-hidden">
